import React, { memo, useEffect, useReducer } from "react";

import { initialState, reducer } from "../Reducer";
import { renderProduct } from "../Constaint";
import { GetDataFromAPI } from "../GetAPI.js";
import ProductItem from "./ProductItem";

const handleAddCart = (id) => {
  console.log(id);
}; 
const Products = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { productList } = state;
  const handleRenderProduct = async () => {
    let data = await GetDataFromAPI();
    dispatch(renderProduct(data));
  };
  useEffect(() => {
    handleRenderProduct();
    return () => {};
  }, []);
  
  return (
    <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3 product__list">
      {console.log(productList)}
      {productList.map((item) => (
        <ProductItem
          key={item.id}
          id={item.id}
          image={item.img}
          name={item.name}
          category={item.category}
          price={item.price}
          onClick={handleAddCart()}
        ></ProductItem>
      ))}
    </div>
  );
};

export default Products;
