import { RENDER_PRODUCT,ADD_CART } from "./Constaint";

// Khởi tạo biến
export const initialState = {
  productList: [],
  CartList: []
};

// Tạo reducer
export const reducer = (state, action) => {
  switch (action.type) {
    case RENDER_PRODUCT:
      return {
        ...state,
        productList: action.payload,
      };
    case ADD_CART:
      return {
        ...state,
        CartList: action.payload,
      };
    default:
      throw new Error("Lỗi");
  }
};
