import React, { Component } from 'react';
import Child from './Child';
const listChildren = [
  {
    id: 1,
    name: "henry",
    avatar: "henry.png",
  },
  {
    id: 2,
    name: "Stella",
    avatar: "Stella.png",
  },
  {
    id: 3,
    name: "Thái",
    avatar: "thai.png",
  },
  {
    id: 4,
    name: "thảo",
    avatar: "thao.png",
  },
  {
    id: 5,
    name: "thủy",
    avatar: "thuy.png",
  },
];
class Parent extends Component {
  // Props dùng để truyền dữ liệu và có thuộc tính read only không được phép chỉnh sửa
  // Để truyền theo từng cấp, chỉ truyền từ cấp cha sang con chứ không được truyền từ cấp ông sang cháu
  fullName = "henry";
  renderChild = ()=>{
    return listChildren.map((item) => Child);
  }
  render() {
    console.log(this.props)
    return (
      <div className="bg-primary p-3 me-5">
        <h1>Parent</h1>
        <h2>{this.props.email}</h2>
        <h2>{this.props.age}</h2>
        <Child
          Setavatar={this.props.setAvatar}
          email={this.props.email}
          age={this.props.age}
          fullName={this.fullName}
        ></Child>
      </div>
    );
  }
}

export default Parent;
