import React, { useEffect, useReducer } from "react";
import axios from "axios";
const endpoint = "https://639c3dee16d1763ab1438a00.mockapi.io/Products";

const GetDataFromAPI = async () => {
  try {
    const response = await axios
      .get(endpoint);
    return response.data;
  } catch (error) {
    // xử trí khi bị lỗi
    console.log(error);
  }
};
const handleRenderProduct = async () => {
  let data = await GetDataFromAPI();
  console.log(data);
};
// Khởi tạo biến
const initialState = {
  productList: []
}
// Tạo action
const RENDER_PRODUCT = "render_product";

const renderProduct = payload =>{
  return {
    type: RENDER_PRODUCT,
    payload
  };
}
// Tạo reducer
const reducer  = (state,action)=>{
  switch (action.type) {
    case RENDER_PRODUCT:
      return {
        ...state,
        productList: [...state.productList,action.payload]
      };
    default:
      throw new Error("Lỗi");
  }
}

const ProductItem =  () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {productList} = state;
  useEffect(() => {
     handleRenderProduct();
    // dispatch(renderProduct());

    return () => {};
  }, []);

  return (
    <div className=" col-md-6 col-lg-4">
      <div className="product__card">
        <div className="product__card-item" data-view="">
          <div className="product__card-img">
            <img className="zoom" id="image" src="" alt="image" />
          </div>
          <div className="product__card-body">
            <div div className="d-flex flex-column justify-content-between ">
              <h3></h3>
              <div className="text-group">
                <h4 className="product__card-text"></h4>
                <p className="product__card-price"></p>
              </div>
            </div>
            <div className="btn-group d-flex justify-content-between align-items-center">
              <button className="btn_primary add-cart" data-id="" type="button">
                Add to cart
              </button>
              <i className="fa-regular fa-heart add-like"></i>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductItem;
