import React, { memo, useEffect, useReducer } from "react";
import axios from "axios";
const endpoint = "https://639c3dee16d1763ab1438a00.mockapi.io/Products";

const GetDataFromAPI = async () => {
  try {
    const response = await axios.get(endpoint);
    return response.data;
  } catch (error) {
    // xử trí khi bị lỗi
    console.log(error);
  }
};

// Khởi tạo biến
const initialState = {
  productList: [],
};

// Tạo reducer
const reducer = (state, action) => {
  switch (action.type) {
    case RENDER_PRODUCT:
      return {
        ...state,
        productList: action.payload,
      };
    default:
      throw new Error("Lỗi");
  }
};
 let convert = new Intl.NumberFormat('it-IT', {
    style: 'currency',
    currency: 'VND'
  });
const ProductItem = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { productList } = state;
  const handleRenderProduct = async () => {
    let data = await GetDataFromAPI();
    dispatch(renderProduct(data));
  };
  useEffect(() => {
    handleRenderProduct();
    return () => {};
  }, []);
  
  return (
    <div  className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3 product__list">
      {console.log(productList)}
      {productList.map((item) => (
        <div key={item.id} className=" col-md-6 col-lg-4">
          <div className="product__card">
            <div className="product__card-item" data-view="{item.id}">
              <div className="product__card-img">
                <img className="zoom" id="image" src={item.img} alt={item.name} />
              </div>
              <div className="product__card-body">
                <div div className="d-flex flex-column justify-content-between ">
                  <h3>{item.name}</h3>
                  <div className="text-group">
                    <h4 className="product__card-text">{item.category}</h4>
                    <p className="product__card-price">
                      {convert.format(item.price)}
                    </p>
                  </div>
                </div>
                <div className="btn-group d-flex justify-content-between align-items-center">
                  <button
                    className="btn_primary add-cart"
                    data-id="{item.id}"
                    type="button"
                  >
                    Add to cart
                  </button>
                  <i className="fa-regular fa-heart add-like"></i>
                </div>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProductItem;
