import { RENDER_PRODUCT } from "./Constaint";

// Khởi tạo biến
export const initialState = {
  productList: [],
};

// Tạo reducer
export const reducer = (state, action) => {
  switch (action.type) {
    case RENDER_PRODUCT:
      return {
        ...state,
        productList: action.payload,
      };
    default:
      throw new Error("Lỗi");
  }
};
