import React, { Component } from 'react'
import Parent from './Parent'
import Uncle from './Uncle';

export default class Grand extends Component {
  email = "thailnp133@gmail.com";
  age = 22;
  // biến hững dữ liệu từ con truyền ra
  avatar = "";
  // hàm set biến hứng, tham số đầu vào là dữ liệu từ con truyền ra
  setAvatar(dataFromChild){
    this.avatar = dataFromChild;
  }
  render() {
    return (
      <div className='bg-success p-3'>
      <h1>GrandPa</h1>
        <div className="d-flex">
          <Parent email={this.email} age={this.age}></Parent>
          <Uncle></Uncle>
        </div>
      </div>
    )
  }
}
