import React, { memo, useEffect, useReducer } from "react";
import axios from "axios";
const endpoint = "https://639c3dee16d1763ab1438a00.mockapi.io/Products";

const GetDataFromAPI = async () => {
  try {
    const response = await axios.get(endpoint);
    return response.data;
  } catch (error) {
    // xử trí khi bị lỗi
    console.log(error);
  }
};

// Khởi tạo biến
const initialState = {
  productList: [],
};
// Tạo action
const RENDER_PRODUCT = "render_product";

const renderProduct = (payload) => {
  return {
    type: RENDER_PRODUCT,
    payload,
  };
};
// Tạo reducer
const reducer = (state, action) => {
  switch (action.type) {
    case RENDER_PRODUCT:
      return {
        ...state,
        productList: [ action.payload],
      };
    default:
      throw new Error("Lỗi");
  }
};

const ProductItem = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { productList } = state;
  const handleRenderProduct = async () => {
    let data = await GetDataFromAPI();
    dispatch(renderProduct(data));
  };
  useEffect(() => {
    handleRenderProduct();
    return () => {};
  }, []);
  return (
    
      <div>
        {productList.map(item=>(
          <div class=" col-md-6 col-lg-4">
          <div class="product__card">
              <div class="product__card-item" data-view="${item.id}">
                <div class="product__card-img">
                <img class="zoom" id="image" src="{item.img}" alt="image"/>
                </div>
                <div class="product__card-body">
                <div div class = "d-flex flex-column justify-content-between " >
                <h3>${item.name}</h3>
                  <div class="text-group">
                    <h4 class="product__card-text">${item.category}</h4>
                    <p class="product__card-price">${convert.format(item.price)}</p>
                    </div>
                </div>
                  <div class="btn-group d-flex justify-content-between align-items-center">
                    <button class="btn_primary add-cart" data-id="${item.id}" type="button">Add to cart</button>
                    <i class="fa-regular fa-heart add-like"></i>
                </div>
                            </div>
              </div>
          </div>
        </div>
        )
        )}
      </div>
  );
};

export default ProductItem;
