import React from "react";

const endpoint = "https://639c3dee16d1763ab1438a00.mockapi.io/Products";

const GetDataFromApi = async () =>{
   try {
     const respone = await fetch(enpoint);
     const data = await respone.json();
     // Kiểm tra xem có chắc chắn là có dữ liệu hay không, và dữ liệu đó có phải là mảng hay không rồi mới render ra giao diện
     if (data.length > 0 && Array.isArray(data)) {
       data.forEach((item) => {
         renderProduct(item);
         let convert = item.category.split(" ")[0].replace("'s", "");
         // console.log(typeof convert);
         // Thêm vào Set
         options.add(convert);
         products.push(item);
         // console.log(products);
       });
     }
   } catch (error) {
     renderSweetAlertError("Có lỗi xảy ra với hệ thống");
     //  document.body.innerHTML = "";
   }
}
const ProductItem = () => {
  return (
    <div className=" col-md-6 col-lg-4">
      <div className="product__card">
        <div className="product__card-item" data-view="">
          <div className="product__card-img">
            <img className="zoom" id="image" src="" alt="image" />
          </div>
          <div className="product__card-body">
            <div div className="d-flex flex-column justify-content-between ">
              <h3></h3>
              <div className="text-group">
                <h4 className="product__card-text"></h4>
                <p className="product__card-price"></p>
              </div>
            </div>
            <div className="btn-group d-flex justify-content-between align-items-center">
              <button className="btn_primary add-cart" data-id="" type="button">
                Add to cart
              </button>
              <i className="fa-regular fa-heart add-like"></i>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductItem;
