import React, { Component } from "react";

class Child extends Component {
  // Props sẽ được  truyền theo cấp lớn nhất đến cấp nhỏ nhất và ngược lại: vd: Ông cố -> Ông nội -> Cha -> Con -> CHÁU -> Con -> Cha-> Ông nội -> Ông cố
  avatar = "AVX.XN";
  // Nhưng dữ liệu truyền đi phải cùng 1 nhánh component
  render() {
    console.log(this.props);
    return (
      <div className="bg-danger p-3 my-5">
        <h1>Child</h1>
        <h2>{this.props.name}</h2>
        <h2>{this.props.avatar}</h2>
        <button onClick={()=>this.props.set(this.avatar)}>Emit avatar</button>
      </div>
    );
  }
}

export default Child;
